using System.Collections;using System.Collections.Generic;using System.IO;using UnityEditor;using UnityEngine;public class ModifyVuforiaTarget : ScriptableWizard{    //Drop down menu for selecting canvas type    //DrawIf property for Enums is based on index value    //Normal is index 0, InstantLinkOpener is index 2.    //public enum CanvasTypeEnum    //{    //    Normal,    //    ComplianceSign,    //    InstantLinkOpener    //}    //public CanvasTypeEnum canvasType = CanvasTypeEnum.Normal;    [Header("Image Target Settings")]    public string targetName = null;    public float width = 1.0f;    public Sprite image = null;    public bool active = true;    public string metaData = null;    static void CreateWizard()    {        ScriptableWizard.DisplayWizard<ModifyVuforiaTarget>("Update Target", "Update");    }    void OnWizardCreate()    {        string imgPath = null;        if(image != null){            Object obj = image;            imgPath = AssetDatabase.GetAssetPath(obj.GetInstanceID());        }        VuforiaTools uploadRequest = new VuforiaTools();        string metaDataUpload = null;        if(metaData!=null){            if(metaData==""){                metaData = null;            }            else{                var plainTextBytes = System.Text.Encoding.UTF8.GetBytes(metaData);                metaDataUpload = System.Convert.ToBase64String(plainTextBytes);            }        }        if (targetName != null)        {            if (targetName == "")            {                targetName = null;            }        }        string returnString = uploadRequest.UpdateImageTarget(titleContent.text, targetName, width, imgPath, active, metaDataUpload);        //string returnString = uploadRequest.UpdateImageTarget(titleContent.text, "Happy", width, null, active, "metadad");        //string returnString = "Testing for now";        if (returnString != "fail")        {            Debug.Log("Vuforia SUCCESS! Target image updated.");        }        else        {            //Debug.Log(titleContent.text);            //Debug.Log(targetName);            //Debug.Log(width);            //Debug.Log(active);            //Debug.Log(metaData);            Debug.Log("Vuforia Upload Failed, try again.");            Debug.Log("Error: " + returnString);            return;        }    }    void OnWizardOtherButton()    {        string path = EditorUtility.OpenFilePanel("Choose MetaData Text File", "", "txt,json");        metaData = File.ReadAllText(path);    }    void OnWizardUpdate()    {        //Updates the buttons when you change the size of the array.        helpString = "Enter canvas details";    }}